import axios from "axios";
export default {
  name: "Project_case",
  data: function data() {
    return {
      env_list: [],
      fd_index: '',
      //fd参数对应行的下标
      choose_tab_pane: '',
      right_api: false,
      right_configure: false,
      userable_par: '',
      setting_api: {},
      setting_configure: {},
      dck: [],
      // 默认选中的节点
      dek: [],
      //默认展开的节点
      ref: "tree",
      tableData: [{
        name: "name",
        des: 'des'
      }],
      activeName: "",
      //body中默认显示哪个tab页
      apis: [// {id:1,type:'api',label:'接口1号',children:[{id:'1_1',type:'configure',label:'设置1'},{id:'1_2',type:'configure',label:'设置2'},{id:'1_3',type:'configure',label:'设置3'}]},
      ],
      response_data: {
        R: '',
        // Response
        RD: '',
        // ResponseData
        CR: '' // ConfigureResult

      }
    };
  },
  props: ["project_id"],
  mounted: function mounted() {
    var _this = this;

    document.getElementById('right_id').style.minHeight = (document.documentElement.clientHeight - 240).toString() + 'px';
    document.getElementById('right_id').style.maxHeight = (document.documentElement.clientHeight - 240).toString() + 'px';
    document.getElementById('left_div').style.height = (document.documentElement.clientHeight - 286).toString() + 'px';
    axios.get('http://localhost:8000/get_apis/', {
      params: {
        project_id: this.project_id
      }
    }).then(function (res) {
      _this.apis = res.data;
    }); // 获取选中的接口

    axios.get('http://localhost:8000/get_dck/', {
      params: {
        project_id: this.project_id
      }
    }).then(function (res) {
      _this.dck = res.data;
    });
    axios.get('http://localhost:8000/get_env_list/').then(function (res) {
      _this.env_list = res.data;
      _this.user_info = _this.loadAll();
    });
  },
  methods: {
    querySearch: function querySearch(queryString, cb) {
      var user = this.user_info;
      var results = queryString ? user.filter(this.createFilter(queryString)) : user; // 调用 callback 返回建议列表的数据

      cb(results);
    },
    createFilter: function createFilter(queryString) {
      return function (user) {
        return user.host.toLowerCase().indexOf(queryString.toLowerCase()) === 0;
      };
    },
    loadAll: function loadAll() {
      return this.env_list;
    },
    handleSelect: function handleSelect(item) {
      this.setting_api.host = item.host;
    },
    get_action_binary: function get_action_binary() {
      return process.env.VUE_APP_BASE_URL + '/upload_binary_file/?ApiID=' + this.setting_api.id;
    },
    get_action_fd: function get_action_fd() {
      return process.env.VUE_APP_BASE_URL + '/upload_fd_file/?ApiID=' + this.setting_api.id;
    },
    get_json: function get_json() {
      try {
        return JSON.parse(this.response_data.R);
      } catch (e) {
        return this.response_data.R;
      }
    },
    // 获取index
    set_fd_index: function set_fd_index(index) {
      this.fd_index = index;
    },
    upload_fd_file: function upload_fd_file(res, file) {
      var file_name = '*FILE*' + this.setting_api.id + '_' + file.raw.name;
      this.setting_api.payload_fd[this.fd_index].value = file_name;
      document.getElementById('fd_value_input_' + this.fd_index).value = file_name;
      document.getElementById('fd_value_input_' + this.fd_index).disabled = true;
    },
    upload_binary_file: function upload_binary_file(res, file) {
      this.setting_api.payload_binary = this.setting_api.id + '_' + file.raw.name;
    },
    up_api: function up_api() {
      var _this2 = this;

      axios.get('http://localhost:8000/up_api/', {
        params: {
          api_id: this.setting_api.id,
          project_id: this.project_id
        }
      }).then(function (res) {
        _this2.apis = res.data;
        _this2.right_api = false;
        _this2.dek = [_this2.setting_api.id];
      });
    },
    down_api: function down_api() {
      var _this3 = this;

      axios.get('http://localhost:8000/down_api/', {
        params: {
          api_id: this.setting_api.id,
          project_id: this.project_id
        }
      }).then(function (res) {
        _this3.apis = res.data;
        _this3.right_api = false;
      });
    },
    up_configure: function up_configure() {
      var _this4 = this;

      axios.get('http://localhost:8000/up_configure/', {
        params: {
          configure_id: this.setting_configure.id,
          project_id: this.project_id
        }
      }).then(function (res) {
        _this4.apis = res.data;
        _this4.right_configure = false;
        _this4.dek = [parseInt(_this4.setting_configure.id.split('_')[0])]; // 将字符串转换成整形
      });
    },
    down_configure: function down_configure() {
      var _this5 = this;

      axios.get('http://localhost:8000/down_configure/', {
        params: {
          configure_id: this.setting_configure.id,
          project_id: this.project_id
        }
      }).then(function (res) {
        _this5.apis = res.data;
        _this5.right_configure = false;
        _this5.dek = [parseInt(_this5.setting_configure.id.split('_')[0])]; // 将字符串转换成整形
      });
    },
    // get_label(label){
    //   if (label.length>12){
    //     var new_label = label.substring(0,12) + '...'
    //   }
    //   else{
    //     var new_label = label
    //   }
    //   return new_label
    //
    // },
    get_label: function get_label(label) {
      return label.substring(0, 12) + (label.length > 12 ? '...' : '');
    },
    handleCheckChange: function handleCheckChange(data, checked) {
      var _this6 = this;

      console.log(data, checked);
      axios.get('http://localhost:8000/set_dck/', {
        params: {
          project_id: this.project_id,
          dck: this.$refs.tree.getCheckedKeys().toString()
        }
      }).then(function (res) {
        _this6.dck = res.data;
      });
    },
    // 如果是接口，则展示接口详情页；否则展示配置详情
    set: function set(data) {
      var _this7 = this;

      console.log(data);

      if (data.type == 'api') {
        this.right_api = true;
        this.right_configure = false;
        this.setting_api = data;
        this.response_data = {
          R: '',
          // Response
          RD: '',
          // ResponseData
          CR: '' // ConfigureResult

        }; // 获取可用变量

        axios.get('/get_userable_par/', {
          params: {
            api_id: data.id,
            project_id: this.project_id
          }
        }).then(function (res) {
          _this7.userable_par = res.data;
        });
      } else {
        this.right_api = false;
        this.right_configure = true;
        this.setting_configure = data;
      }
    },
    // 删除，增加二次确认
    remove: function remove(data) {
      var _this8 = this;

      if (confirm("确定删除吗？") == false) {
        return;
      }

      axios.get('http://localhost:8000/remove_ac', {
        params: {
          project_id: this.project_id,
          id: data.id
        }
      }).then(function (res) {
        _this8.apis = res.data;
        _this8.right_api = false; // 删除后设置右侧不显示

        _this8.right_configure = false;
      });
    },
    v_if: function v_if(data) {
      if (data.type == 'api') {
        return true;
      } else {
        return false;
      }
    },
    handleClick: function handleClick(tab, event) {
      console.log(tab, event);
    },
    // 新增配置后，让该接口展开
    add_configure: function add_configure(data) {
      var _this9 = this;

      axios.get('http://localhost:8000/add_configure/', {
        params: {
          project_id: this.project_id,
          id: data.id
        }
      }).then(function (res) {
        _this9.apis = res.data;
        _this9.dek = [data.id];
      });
    },
    add_apis: function add_apis() {
      var _this10 = this;

      axios.get('http://localhost:8000/add_apis/', {
        params: {
          project_id: this.project_id
        }
      }).then(function (res) {
        _this10.apis = res.data;
        _this10.dek = [];
      });
    },
    save_configure: function save_configure() {
      var _this11 = this;

      axios.post('http://localhost:8000/save_configure/', this.setting_configure).then(function (res) {
        _this11.$message({
          message: '保存成功！',
          type: 'success'
        });
      });
    },
    save_api: function save_api() {
      var _this12 = this;

      axios.post('http://localhost:8000/save_api/', this.setting_api, {
        params: {
          project_id: this.project_id
        }
      }).then(function (res) {
        _this12.apis = res.data;

        _this12.$message({
          message: '保存成功',
          type: 'success'
        });
      });
    },
    send_api: function send_api() {
      var _this13 = this;

      axios.post('http://localhost:8000/send_api/', this.setting_api, {
        params: {
          project_id: this.project_id
        }
      }).then(function (res) {
        _this13.response_data = res.data;
        _this13.choose_tab_pane = 'Response';
      });
    },
    run: function run() {
      if (confirm('执行大用例之前请确保当前接口或配置的设置已经保存') == false) {
        return;
      }

      var end_result = document.getElementById('end_result');
      var doing_api = document.getElementById('doing_api');
      var project_id = this.project_id;
      var t = setInterval(function () {
        axios.get('/doing_api/', {
          params: {
            project_id: project_id // 由于作用域的问题，定时器无法获取this.project_id,所以在外面定义变量var

          }
        }).then(function (res) {
          doing_api.innerText = res.data;
        });
      }, 500);
      axios.get('/run/', {
        params: {
          project_id: this.project_id,
          dck: this.dck.toString()
        }
      }).then(function (res) {
        clearInterval(t);
        end_result.innerText = res.data;

        if (res.data == 'True') {
          end_result.style.color = 'green';
        } else {
          end_result.style.color = 'red';
        }

        doing_api.innerText = '全部执行完毕';
      });
    },
    report: function report() {},
    // 选择配置的方法，切换配置tab页时清空之前选择的方式、值
    choose_configure_method: function choose_configure_method(tab, event) {
      console.log(tab, event);
      this.setting_configure.select = '';
      this.setting_configure.value = '';
      this.setting_configure.method = tab.label;
    },
    // 新增参数
    add_params: function add_params() {
      this.setting_api.params.push({
        "key": "",
        "value": ""
      });
    },
    // 删除参数
    del_params: function del_params(index) {
      this.setting_api.params.splice(index, 1);
    },
    // 新增参数
    add_headers: function add_headers() {
      this.setting_api.headers.push({});
    },
    // 删除参数
    del_headers: function del_headers(index) {
      this.setting_api.headers.splice(index, 1);
    },
    // 新增参数
    add_payload_fd: function add_payload_fd() {
      this.setting_api.payload_fd.push({});
    },
    // 删除参数
    del_payload_fd: function del_payload_fd(index) {
      this.setting_api.payload_fd.splice(index, 1);
    },
    // 新增参数
    add_payload_xwfu: function add_payload_xwfu() {
      this.setting_api.payload_xwfu.push({});
    },
    // 删除参数
    del_payload_xwfu: function del_payload_xwfu(index) {
      this.setting_api.payload_xwfu.splice(index, 1);
    }
  }
};
